package org

import org.days.*

fun main() {
    while(true){
        println("---------------select a day---------------")
        val puzzleNumber = readLine()

        when(puzzleNumber){
            "1" -> day1()
            "2" -> day2()
            "q" -> break
            else -> println("Puzzle ainda nao implementado")
        }
    }
    System.exit(0)
}
